import React from "react";
import "./directory.style.scss";
import MenuItem from "../menu-item/menu-item.component";

class Directory extends React.Component {
  constructor() {
    super();

    this.state = {
      productSection: [
        {
          id: 1,
          title: "Beauty",
          image:
            "https://www.planetorganic.com/images/categories/small_AUTO_VeganBeauty.jpg",
          sectionLink: "Beauty",
        },

        {
          id: 2,
          title: "Health and Fitness",
          image:
            "https://www.planetorganic.com/images/categories/small_AUTO_VeganHealth.jpg",
          sectionLink: "health",
        },
        {
          id: 3,
          title: "Groceries",
          image:
            "https://www.planetorganic.com/images/categories/small_AUTO_VeganGroceries.jpg",
          sectionLink: "Groceries",
        },
        {
          id: 4,
          title: "Offers and Discounts",
          image:
            "https://www.planetorganic.com/images/categories/small_VEGAN_OFFERS.jpg",
          size: "large",
          sectionLink: "Offers",
        },
        {
          id: 5,
          title: "Clothing",
          image:
            "https://www.planetorganic.com/images/categories/small_VEGAN_OFFERS.jpg",
          size: "large",
          sectionLink: "Clothing",
        },
      ],
    };
  }

  render() {
    return (
      <div className="directory-menu">
        {/*pass all the other propes to the menu component thats what other prop does */}
        {this.state.productSection.map(({ id, ...otherprops }) => {
          return <MenuItem key={id} {...otherprops} />;
        })}
      </div>
    );
  }
}

export default Directory;
