import { signInWithGoogle } from "../../firebase/firebase.util";

export const setCurrentUser = (user) => ({
  type: "SET_CURRENT_USER",
  payload: user,
});

export const signInWithGoogleMail = () => {
  return (dispatch) => {
    signInWithGoogle().then(success=>
        {
            alert ('yep')
        }).catch(err=>{
            alert('n')
        })
  };
};
