const INITIAL_STATE = {
  hidden: true,
  caretItems: [],
};

const cartReducer = (state = INITIAL_STATE, action) => {
  switch (action.type) {
    case "TOGGLE_CART_HIDDEN":
      return {
        ...state,
        hidden: !state.hidden,
      };

    case "ADD_ITEM":
      return {...state,caretItems:[...state.caretItems,action.payload] };
    default:
      return state;
  }
};
export default cartReducer;
