import React from "react";
import { Switch, Route } from "react-router-dom";
import HomePage from "./pages/homepage/homepage.component";
import ShopPage from "./pages/shop/shoppage.component";
import SignInAndSignUpPage from "./pages/signinandsignup/sign-in-and-signup";
import Header from "./components/header/header.component";
import { auth, createUser } from "./firebase/firebase.util";
import {setCurrentUser} from './redux/user/user-action'
import {connect} from "react-redux"

class App extends React.Component {


  unsubscribeFromAuth = null;y

  componentDidMount() {
    this.unsubscribeFromAuth = auth.onAuthStateChanged(async (userAuth) => {

      if (userAuth) {
        this.setState({ currentUser: userAuth });
        const userRef = createUser(userAuth, userAuth.displayName,'called from sigin');
        (await userRef).onSnapshot((Snapshot) => {
          this.setState({
            currentUser: {
              id: Snapshot.id,
              ...Snapshot.data(),
            },
          });
        });

        return;
      }

      this.setState({ currentUser: userAuth });
    });
  }

  componentWillUnmount() {
    this.unsubscribeFromAuth();
  }

  render() {
    return (
      <div>
        <Header currentUser={this.state.currentUser} />
        <Switch>
          <Route exact path="/" component={HomePage} />
          <Route path="/shop" component={ShopPage} />
          <Route path="/signin" component={SignInAndSignUpPage} />
        </Switch>
      </div>
    );
  }
}
const mapDispatchToProps=dispatch=>({
  setCurrentUser:user=>dispatch(setCurrentUser(user))


})


export default connect(null,mapDispatchToProps) (App);
