import React from "react";
import CustomButton from "../button-component/button.component";
import './cart-dropdown-component.scss'
import {connect} from 'react-redux'
import CartItem from "../cart-item-component/cart.item.component";
import{selectCartItems} from '../../redux/cart/cart-selector'
import {Redirect} from 'react-router-dom'



const CartDropdown = ({cartItems}) =>( <div>
    <div className='cart-dropdown'>
        <div className='cart-items'>
            {
                cartItems.length?cartItems.map(cartItem=>(<CartItem key={cartItem.id} item={cartItem}/>)):<span className='empty-message'>Your cart is empty</span>}
        
        </div>
        <CustomButton onClick={()=><Redirect path='/'/>}>GO TO CHECKOUT</CustomButton>


        </div>

         
  

</div>);
//state will pass the state store and from the state extract cart and from the cart extract cartItems
const mapStateToProps=(state)=>({
    cartItems:selectCartItems(state)
})
export default connect(mapStateToProps)(CartDropdown)
