import React from "react";
import CollectionOverview from "../../components/collection-overview/collection-overview.component";
import { Switch, Route } from "react-router-dom";
import {connect} from 'react-redux'
import withSpinner from '../../components/with-spinner-component/with-spinner.component'
import {updateCollections} from '../../redux/shop/shop-action'
import Collection from "../collection/collection.component";
import {firestore,convertCollectionSnapshotToMap} from "../../firebase/firebase.util";
const CollectionWithSpinner=withSpinner(Collection)


class ShopPage extends React.Component {
    constructor()
    {
      super()

      this.state={

        loading:'true'
      }




    }

  componentDidMount() {
    console.log('THE SHOP COMPOENETssssssssssssssssssssssssss')
    const{updateCollections}=this.props
    const collectionRef = firestore.collection("collection");
    collectionRef.onSnapshot((docSnapshot) => {
     const collectionMap=convertCollectionSnapshotToMap(docSnapshot)
      updateCollections(collectionMap)
      this.setState({loading:'false'})
    
    });
    
  }

  render() {
    const { match } = this.props;
    return (
      <div>
        <Switch>
          <Route exact path={`${match.path}`} component={CollectionOverview} />

          <Route path={`${match.path}/:collectionId`} component={CollectionWithSpinner} />
        </Switch>
      </div>
    );
  }
}

const mapDispatchToProps=dispatch=>({
  updateCollections:collecionsMap=>dispatch(updateCollections(collecionsMap))
})
export default connect(null,mapDispatchToProps)(ShopPage);
