import React from "react";
import CollectionOverview from "../../components/collection-overview/collection-overview.component";
import { Switch, Route } from "react-router-dom";
import { connect } from "react-redux";
import withSpinner from "../../components/with-spinner-component/with-spinner.component";
import { asyncFetchCollection } from "../../redux/shop/shop-action";
import Collection from "../collection/collection.component";
const CollectionWithSpinner = withSpinner(Collection);
const CollectionviewWithSpinner = withSpinner(CollectionOverview);

class ShopPage extends React.Component {
  componentDidMount() {
   const{asyncFetchCollection}=this.props
   asyncFetchCollection()
  }

  render() {
    const { match,isLoading } = this.props;
    console.log(this.props)
    return (
      <div>
        <Switch>
          <Route
            exact
            path={`${match.path}`}
            render={(props) => <CollectionviewWithSpinner />}
          />

          <Route
            path={`${match.path}/:collectionId`}
            render={(props) => (
              <CollectionWithSpinner
                isloading={this.state.loading}
                {...props}
              />
            )}
          />
        </Switch>
      </div>
    );
  }
}

const mapDispatchToProps = (dispatch) => ({
  asyncFetchCollection: () => dispatch(asyncFetchCollection()),
});
const mapStateToProp = (state) => 
({
  isLoading:state.shop.isFetching
})


export default connect(mapStateToProp, mapDispatchToProps)(ShopPage);
