import React from "react";
import Homepage from "./pages/homepage/homepage.component";
import { Switch, Route } from "react-router-dom";
import ShopPage from "./pages/shop/shoppage.component";
import Header from "./components/header/header.component";
import SiginAndSignup from "./pages/signinandsignup/sign-in-and-signup";
import firebase from "firebase/app";

class App extends React.Component {
  constructor() {
    super();
    this.state = {
      currentUser: null,
    };
  }
  unsubscribeFromAuth=null
  componentDidMount() {
  this.unsubscribeFromAuth=  firebase.auth().onAuthStateChanged(user=> {
      if (user) {
        this.setState({currentUser:user.displayName})


      }
    });
  }
  componentWillUnmount()
  {
    this.unsubscribeFromAuth()
  }
  render() {
    return (
      <div>
        <Header currentUser={this.state.currentUser}/>
        <Switch>
          <Route exact path="/" component={Homepage} />
          <Route path="/shop" component={ShopPage} />
          <Route path="/signin" component={SiginAndSignup} />
        </Switch>
      </div>
    );
  }
}

export default App;
