const INITIAL_STATE = {
  collections:null,
  isFetching:false,
  error:null
  
};
const shopReducer = (state = INITIAL_STATE, action) => {
  console.log(action)
  switch (action.type) {
    
    case "UPDATE_COLLECTIONS":
      return {...state,collections:action.payload};

    default:
      return state;
  }
};

export default shopReducer;
