import React from "react";
import { Switch, Route, Redirect } from "react-router-dom";
import HomePage from "./pages/homepage/homepage.component";
import ShopPage from "./pages/shop/shoppage.component";
import Header from "./components/header/header.component";
import { auth, createUser,addDatatoFireBase } from "./firebase/firebase.util";
import { setCurrentUser } from "./redux/user/user-action";
import { connect } from "react-redux";
import { selectCurrentUser } from "./redux/user/user-selector";
import SiginAndSignup from "./pages/signinandsignup/sign-in-and-signup";
import Checkout from "./pages/checkout-component/checkoutpage.component";
import {selectShopItems } from './redux/shop/shop-selector'


class App extends React.Component {
  unsubscribeFromAuth = null;

  componentDidMount() {
    //Set current user will be sent as a prop to app component from the redux store
    //we can use this action from the redux store
    //destructure setCurrentUser from this.props
    const { setCurrentUser,selectShopItems } = this.props;

    this.unsubscribeFromAuth = auth.onAuthStateChanged(async (userAuth) => {
      addDatatoFireBase('collection',selectShopItems)

      if (userAuth) {
        //If userdata dosen't exist then create a new users
         console.log(userAuth)
        const userRef = createUser(
          userAuth,
          userAuth.displayName,
          "called from sigin"
        );
        //returns all the data related to the logged in user merged with user Auth details
        (await userRef).onSnapshot((Snapshot) => {
          setCurrentUser({ id: Snapshot.id, ...Snapshot.data() });
        });

        return;
      }
      //if user is logged out set the state to null
      console.log(userAuth)
      setCurrentUser(userAuth);
    });
  }

  componentWillUnmount() {
    this.unsubscribeFromAuth();
  }

  render() {
    return (
      <div>
        <Header />
        <Switch>
          <Route exact path="/" component={HomePage} />
          <Route path="/shop" component={ShopPage} />
          {/*if the user is signed in render the root page else render the authentication page*/}
          <Route
            exact
            path="/signin"
            render={() =>
              this.props.currentUser ? <Redirect to="/" /> : <SiginAndSignup />
            }
          />
            <Route path="/checkout" component={Checkout} />

        </Switch>
      </div>
    );
  }
}
//current user is passed as props from the redux store
const mapStateToProps = (state) => ({
  currentUser: selectCurrentUser(state),
  collectionItems:selectShopItems(state)
});

const mapDispatchToProps = (dispatch) => ({
  setCurrentUser: (user) => dispatch(setCurrentUser(user)),
});

export default connect(mapStateToProps, mapDispatchToProps)(App);
