import {
  signInWithGoogle,
  createUser,
  getCurrentUser,
} from "../../firebase/firebase.util";
import { auth } from "../../firebase/firebase.util";

export const setCurrentUser = (user) => ({
  type: "SET_CURRENT_USER",
  payload: user,
});

export const signInWithGoogleMail = () => {
  return (dispatch) => {
    signInWithGoogle()
      .then(({ user }) => {
        createUser(user, user.displayName).then((userRef) => {
          userRef.onSnapshot((onSnapshot) => {
            dispatch(
              setCurrentUser({
                id: onSnapshot.id,
                data: { ...onSnapshot.data() },
              })
            );
          });
        });
      })
      .catch((err) => {
        alert(err);
      });
  };
};

export const signInWithEmailAndPasword = (email, password) => {
  return (dispatch) => {
    auth
      .signInWithEmailAndPassword(email, password)
      .then(({ user }) => {
        createUser(user, user.displayName).then((userRef) => {
          userRef.onSnapshot((onSnapshot) => {
            dispatch(
              setCurrentUser({
                id: onSnapshot.id,
                data: { ...onSnapshot.data() },
              })
            );
          });
        });
      })
      .catch((err) => {
        alert(err);
      });
  };
};

export const userState = () => {
  return (dispatch) => {
    getCurrentUser()
      .then((userRef) => {
        getUserData(userRef);
      })
      .catch((err) => console.log(err));
  };
};
export const signOut = () => {
  type: "SIGN OUT";
};

const getUserData = (userRef) => {
  createUser(userRef, userRef.displayName).then((userRef) => {
    userRef.onSnapshot((onSnapshot) => {
      dispatch(
        setCurrentUser({
          id: onSnapshot.id,
          data: { ...onSnapshot.data() },
        })
      );
    });
  });
};
