import React from "react";
import CollectionOverview from "../../components/collection-overview/collection-overview.component";
import { Switch, Route } from "react-router-dom";
import {connect} from 'react-redux'
import withSpinner from '../../components/with-spinner-component/with-spinner.component'
import {asyncFetchCollection} from '../../redux/shop/shop-action'
import Collection from "../collection/collection.component";
const CollectionWithSpinner=withSpinner(Collection)
const CollectionviewWithSpinner=withSpinner(CollectionOverview)


class ShopPage extends React.Component {
    constructor()
    {
      super()

      this.state={

        loading:true
      }




    }

  componentDidMount() {
    
    const{updateCollections}=this.props

  
    
  }

  render() {
    const { match } = this.props;
    return (
      <div>
        <Switch>
          <Route exact path={`${match.path}`} render={(props)=><CollectionviewWithSpinner/>} />

          <Route path={`${match.path}/:collectionId`} render={(props)=> <CollectionWithSpinner isloading={this.state.loading}{...props}/>} />
        </Switch>
      </div>
    );
  }
}

const mapDispatchToProps=dispatch=>({
  updateCollections:collecionsMap=>dispatch(asyncFetchCollection())
})
const mapStateToProp=(state)=>
({

  isloading:state.shop.isFetching





})
export default connect(null,mapDispatchToProps)(ShopPage);
