import { addMultiplier } from "./cart-utility";

const INITIAL_STATE = {
  hidden: true,
  cartItems: [],
};

const cartReducer = (state = INITIAL_STATE, action) => {
  switch (action.type) {
    case "TOGGLE_CART_HIDDEN":
      return {
        ...state,
        hidden: !state.hidden,
      };

    case "ADD_ITEM":
      return {...state,cartItems:addMultiplier(state.cartItems,action.payload) };
    default:
      return state;
  }
};
export default cartReducer;
