import React from "react";
import "./checkoutitem.component.scss";
import {removeItem} from '../../redux/cart/cart-action'
import  {connect} from 'react-redux'
const CheckoutItem = ({ cartitem: { name, imageUrl, price, quantity,id } }) => (
  <div className="checkout-item">
    <div className="image-container">
      <img src={imageUrl} alt="item"></img>
    </div>
    <span className="name">{name}</span>
    <span className="quantity">{quantity}</span>
    <span className="price">{price}</span>
    <span className="remove-button" onClick={removeItem()}>&#10005;</span>
  </div>
);
const mapDispatchToProps = (dispatch) => ({
  removeItem: (item) => dispatch(removeItem(item)),
 });
 
export default connect(mapDispatchToProps) (CheckoutItem)
