export const addMultiplier = (cartItems,currentItems) => {
    const Item= cartItems.findIndex(cartItem => cartItem.id === currentItems.id);
    if(Item)
    {
        cartItems[Item].quantity = "Laila"
      
      
    }

    return [...cartItems,{...currentItems,quantity:1}]
};
