import { createSelector } from "reselect";
const SHOP_ID={hats:1,
  sneakers:2,
  jackets:3,
  mens:4,
  womens:5







}
const selectShopData = (state) => state.shop;


export const selectShopItems = createSelector(
  [selectShopData],
  (selectShopData) => selectShopData.collections
);
export const selectCollectionsForPreview = createSelector(
  [selectShopItems],
  collections => Object.keys(collections).map(key => collections[key])
);
export const selectCollectionCategory=(collectionId,state)=>
{
  const shop=selectShopData(state)
  console.log('from selec')
  console.log(state.shop.collections[collectionId])
  return Object.keys(shop.collections).map(collectionKey=>shop.collections[collectionKey]).find(currentCollection=>currentCollection.id===SHOP_ID[collectionId])
}
