import React from "react";
import FormInput from "../form-input-component/form-input.component";
import CustomButton from "../button-component/button.component";

class Sigin extends React.Component {
  constructor() {
    super();
    this.state = {
      email: "",
      password: "",
    };
  }
  handlesubmit = (event) => {
    event.preventDefault();
    this.setState({ email: "", password: "" });
  };
  handlechange = (event) => {
    //pull value of name and event
    const { value, name } = event.target;
    this.setState({ [name]: value });
  };
  render() {
    return (
      <div className="sigin">
        <h2>Already have an account?</h2>
        <span>Sigin with email and password</span>
        <form onSubmit={this.handlesubmit}>
          {/*Create a custom form input and pass all the necessary props and handle change which will change the state */}
          <FormInput
            name="email"
            type="email"
            required="true"
            value={this.state.email}
            handlechange={this.handlechange}
            label="email"
          />

          <FormInput
            name="password"
            type="password"
            required="true"
            value={this.state.password}
            onChange={this.handlechange}
            label="password"
          />

          <CustomButton value="submit">Submit</CustomButton>
        </form>
      </div>
    );
  }
}

export default Sigin;
