export const addMultiplier = (cartItems,currentItems) => {
    const Item= cartItems.find(cartItem => cartItem.id === currentItems.id);
    if(Item)
    {
        //iterate through if item found return an array with currentitem quantity added on
       return  cartItems.map(item=>{

                if(item.id===currentItems.id)
                {
                       return item.quantity+1
                }



        })
    }
    return [...cartItems,{...currentItems,quantity:1}]
};
