import { createSelector } from "reselect";
const SHOP_ID={hats:1,
  sneakers:2,
  jackets:3,
  mens:4,
  womens:5







}
const selectShopData = (state) => state.shop;


export const selectShopItems = createSelector(
  [selectShopData],
  (selectShopData) => selectShopData.collections
);
export const selectCollectionsForPreview = createSelector(
  [selectShopItems],
  collections => Object.keys(collections).map(key => collections[key])
);
export const selectCollectionCategory=function(state,collectionId)
{
    
  createSelector([selectShopItems],collections.find(collection=>collection.id==SHOP_ID[collectionId]))


  
}
