  import { createSelector } from 'reselect'

const INITIAL_STATE={
    currentUser: null

}

export const userSelector=createSelector([INITIAL_STATE], userReducer=(state=INITIAL_STATE,action)=>{
    switch(action.type)
{
    case 'SET_CURRENT_USER':

    return{
        ...state,
        currentUser:action.payload
    }




    default:
        return state;

}

})

export default userReducer
